import React from 'react';
import '../App.css';
import './styles/HeroSection.css';

function HeroSection() {
  return (
    <>
      <div class="container">
        <div class="content-container">

            <div class="featured-content">
            <div className="movie-list-item">
                <h1 class="movie-list-title-1">MOST WATCHED THIS WEEK</h1>
                {/* <img class="movie-list-item-image" src="https://dailydead.com/wp-content/uploads/2021/10/Scream-1000.jpg" alt=""></img> */}
                </div>
                <span class="movie-list-item-title">Scream</span>
                <p class="featured-desc">The film follows Theodore Twombly (Joaquin Phoenix), a man who develops a relationship with Samantha (Scarlett Johansson), an artificially intelligent virtual assistant personified through a female voice</p>
                < button class="featured-button">PLAY</button>
            </div>

            </div>

            </div>



    </>
  );
}

export default HeroSection;
